INSERT INTO `v2_pendaftaran_proposal` (`nama_mahasiswa`, `nim`, `email`, `nomor_ponsel`, `nomor_ponsel_orang_tua`, `judul_proposal`, `nama_pembimbing_1`, `nama_grup_riset`, `deskripsi_singkat`, `literatur_utama`, `literatur_penunjang`, `form_kesediaan_membimbing`, `catatan_khusus`, `draft_proposal`, `revisi_draft_proposal_1`, `revisi_draft_proposal_2`, `id`) VALUES
('Adhika Kurnia Febrianto', '141723017', 'sidhika99@gmail.com', '085853656141', '085853656141', 'Sistem Analisis Permintan Darah di Palang Merah Indonesia Unit Tranfusi Darah Kabupaten Bojonegoro', 'Kolonel Lek Dr. Arwin Datumaya Wahyudi Sumari', 'INFORMATION SYSTEM', 'Palang Merah Indonesia (PMI) adalah sebuah organisasi perhimpunan nasional di Indonesia yang bergerak di bidang sosial kemanusiaan. PMI berperan sebagai penyedia jasa kebutuhan darah, membuat PMI dituntut memnuhi kebutuhan pasien. Penelitian ini mengamati persediaan darah di PMI Unit Transfusi Darah (UTD) Kabupaten Bojonegoro. Karena sifat darah yang perishable, maka produk darah memiliki batas umur masing-masing. Sehingga dibutuhkan suatu sistem pengendalian persediaan untu menghindari kerusakan darah. Tujuan dari penelitian ini adalah memberikan analisis peramalan persediaan untuk periode kedepan. Peramalan dilakukan untuk jangka panjang dengan menggunakan metode dengan tingkat error terkecil.', 'Support Vector Regression', '', 'https://drive.google.com/open?authuser=0&usp=forms_web&id=1FWWs2l86RKx714EivDX8Braa5l37iJ09', '', '', '', '', 1),
('NIKO RIZKY', '1431140137', 'XSIRHAAN@GMAIL.COM', '081331993754', '081233183222', 'Aplikasi Pengenalan Plat Nomor Kendaraan Bermotor dengan Metode Deep Learning', 'Dwi Puspitasari, S.Kom., M.Kom.', 'COMPUTER VISION', 'Pengenalan Plat Nomor dengan metode deep learning dan pengolahan citra sehingga menghasilkan output yaitu Nomor kendaraan berdasarkan citra yang didapat oleh kamera', 'Deep Learning', 'Tenserflow atau yang lain', 'https://drive.google.com/open?authuser=0&usp=forms_web&id=1z4GhDWvSkBR5dIO_vx6FCY19wLSOpYG8', '', 'https://drive.google.com/open?authuser=0&usp=forms_web&id=1vn6UwaOAes9DA0HLAG6DKKwKdLNZydSQ', '', '', 2),
('Ricky yulian adi pratama', '1541180038', 'ricky.ghifary1122@gmail.com', '082211297101', '085204941195', 'SISTEM INFORMASI PREDIKSI TINGKAT KRIMINALITAS DENGAN MENGGUNAKAN METODE WINTERS EXPONENTIAL SMOTHING ', 'Kolonel Lek Dr. Arwin Datumaya Wahyudi Sumari', 'INFORMATION SYSTEM', 'Selama ini di Polres Kabupaten Probolinggo bertugas menyelanggarakan tugas pokok Polri dalam rangka memelihara\nkeamanan dan ketertiban masyarakat, menegakkan hukum, serta memberikan perlindungan, mengayomi\nkepada masyarakat. Dari permasalahan yang terjadi khusunya dalam tindakan kriminalitas masyarakat yang ada di wilayah Probolinggo dan agar tidak terjadi kenaikan tindakan kriminal diwilayah Probolinggo di awal bulan maka\ndiperlakukan pembenahan sistem peramalan yang nantinya dapat meramalkan hasil tindakan kriminal untuk\nbulan mendatang. Metode Exponential Smoothing itu menjadi tiga, yaitu Single , Double, dan Triple\n(Winters). Metode Single Exponential Smoothing sangat tepat di gunakan ketika pola datanya mempunyai\nsifat musiman, metode Double Exponential Smoothing sangat efektif digunakan ketika pola data bersifat\ndata trend (kenaikan) dan Triple Exponential Smoothing (Winters) sangat tepat digunakan ketika pola data\nbersifat musiman dan trend (kenaikan). Karena tingkat kriminalitas tersebut bersifat musiman (per-bulan)\ndan trend (kenaikan), maka metode yang tepat untuk meramalkan tingkat kriminalitas di Probolinggo yakni\nmenggunakan metode Winters Exponential Smoothing.', 'Winter Exponential Smothing', 'Simple Moving Average', 'https://drive.google.com/open?authuser=0&usp=forms_web&id=1PK4Ru4QRaWdAREJ_xQJ3DdYjqVEpxSEd', '', 'https://drive.google.com/open?authuser=0&usp=forms_web&id=1YgF0Gm1cArH8FwKxJnZxRmtzSs5q6KzW', '', '', 3),
('Ayu Maulina Diyah', '1541180052', 'ayumaulinna17@gmail.com', '085794551512', '085549014929', 'IMPLEMENTASI METODE FUZZY TIME SERIES PADA SISTEM PERAMALAN KEPADATAN JALAN RAYA KOTA MALANG KHUSUSNYA JL. SOEKARNO HATTA \n', 'Erfan Rohadi, ST., M.Eng., Ph.D.', 'INFORMATION SYSTEM', 'Meramalkan kepadatan kendaran di Jl. Soekarno Hatta Malang. Dan dari hasil prediksi ini akan ditentukan langkah - langkah yang tepat untuk menekan laju pertumbuhan jumlah kendaraan.', 'FUZZY TIME SERIES ', '', 'https://drive.google.com/open?authuser=0&usp=forms_web&id=1vuS33pmhYIG-mWFI2rBoPJeiEXR2kCkO', 'Untuk sementara kalimat judul skripsi belum di fix kan oleh pembimbing', 'https://drive.google.com/open?authuser=0&usp=forms_web&id=1TzRJ0G9T9wjDQUFdr2N2GPGup8Tb8ewm', '', '', 4),
('Jayanti Ayu Saktifa Sari', '1541180229', 'jayantiayuss@gmail.com', '08970424307', '081249303548', 'SISTEM PAKAR PENDETEKSI KERUSAKAN PADA KAMERA DSLR (DIGITAL SINGLE LENS REFLEX) DENGAN MENGGUNAKAN METODE CASE BASED REASONING DAN CERTAINTY FACTOR  ', 'Erfan Rohadi, ST., M.Eng., Ph.D.', 'SISTEM CERDAS', 'Membuat sebuah Sistem Pakar yang dapat mendeteksi kerusakan kamera DSLR (Digital Single Lens Reflex) dengan cara pengguna memasukkan gejala-gejala kerusakan yang muncul pada kameranya, lalu sistem akan memproses hingga mengeluarkan output berupa nama kerusakannya yang dialami dan solusi penanganannya.', 'Case Based Reasoning', 'Certainty Factor', 'https://drive.google.com/open?authuser=0&usp=forms_web&id=1hyBLEu24UKSY0b4VbQmb5sNcqtF1ROpL', 'Penulisan Judul masih belum fix, masih perlu konfirmasi dari Bapak Erfan selaku pembimbing. Karena beliau masih tugas ke luar kota, saya disuruh menunggu untuk bertemu beliau kembali insyaAllah pada hari Senin, 02 Desember 2019', 'https://drive.google.com/open?authuser=0&usp=forms_web&id=1jgf_LlRVt8H20ObOu5mEFR0Mwsa17IPw', '', '', 5),
