(101, 25, '1641720019', 'PENGUJI_1', 75, 80, 75, 0, 0, 0, '1. perbaiki pengujian, tambah node lokasi camera, waktu2 sibuk, dan perbaiki data latih.\r\n2. perbaiki tata tulis', 'LULUS_DENGAN_REVISI'),
(105, 5, '1741723012', 'PENGUJI_2', 75, 78, 70, 0, 0, 0, 'pengujian untuk kelas yg tidak terdaftar bagaimana\r\n', 'LULUS_DENGAN_REVISI');
